import { HttpClient, HttpErrorResponse } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { catchError, tap } from "rxjs/operators";
import {Subject, throwError} from "rxjs";
import { User } from "./user.model";

export interface AuthResponseData{
    idToken: string;
    email: string;
    refreshToken: string;
    expiresIn: string;
    localId: string;
    registered?: boolean
}

@Injectable({providedIn: 'root'})
export class AuthService{
    user = new Subject<User>();
    constructor(private http: HttpClient){

    }
    signup(email: string, password: string){
        return this.http.post<AuthResponseData>('https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCQLl1VXnIA_HXHDVU_Kb4GqrSi3w2wP0k',
        {
            email: email,
            password: password,
            returnSecureToken: true
        }).pipe(catchError(this.handleError),tap(resData=>{

        }));
    }
    login(email:string, password: string){
        return this.http.post<AuthResponseData>('https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyCQLl1VXnIA_HXHDVU_Kb4GqrSi3w2wP0k',
        {
            email:email,
            password: password,
            returnSecureToken: true
        }).pipe(catchError(this.handleError));
    }

    private handleAuthentication(email:string, token:string, expiresIn:number){
        const expirationDate = new Date(new Date().getTime() + +resData.expiresIn*1000);
        const user = new User(resData.email, resData.localId, resData.idToken, expirationDate);
        this.user.next(user);
    }

    private handleError(errorRes:HttpErrorResponse){
        let errorMessage='An unknown error occurred';
        if(!errorRes.error||errorRes.error.error){
          return throwError(errorMessage);
        }
        switch(errorRes.error.error.message){
          case 'EMAIL_EXISTS':
            errorMessage='This email exists'
            break;
          case 'EMAIL_NOT_FOUND':
            errorMessage='Email not found'
            break;
          case 'INVALID_PASSWORD':
            errorMessage='Password not valid'
            break;

        }
        return throwError(errorMessage)
    }
}
